import React, { useState } from "react";
import "./App.css";
import wordBank from "./word_bank.json";

const ROUNDS_PER_GAME = 4;
const MAX_MISTAKES = 3;

function shuffle(array) {
  const arr = [...array];
  for (let i = arr.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [arr[i], arr[j]] = [arr[j], arr[i]];
  }
  return arr;
}

function buildRounds() {
  const allGames = [...wordBank.games];
  // random subset of games
  const shuffledGames = shuffle(allGames);
  const selected = shuffledGames.slice(0, ROUNDS_PER_GAME);

  return selected.map((g) => {
    const cards = shuffle([
      { text: g.word1, isImposter: false },
      { text: g.word2, isImposter: false },
      { text: g.word3, isImposter: false },
      { text: g.word4, isImposter: false },
      { text: g.imposter, isImposter: true }
    ]);

    return {
      category: g.category,
      cards
    };
  });
}

function App() {
  const [rounds, setRounds] = useState(buildRounds);
  const [currentRoundIndex, setCurrentRoundIndex] = useState(0);
  const [mistakesLeft, setMistakesLeft] = useState(MAX_MISTAKES);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [wrongIndices, setWrongIndices] = useState([]);
  const [message, setMessage] = useState("");
  const [gameOver, setGameOver] = useState(false);
  const [finishedAllRounds, setFinishedAllRounds] = useState(false);

  const currentRound = rounds[currentRoundIndex];

  const handleCardClick = (card, index) => {
    if (gameOver || finishedAllRounds) return;
    if (!currentRound) return;
    if (selectedIndex !== null) return; // already found imposter
    if (wrongIndices.includes(index)) return;

    if (card.isImposter) {
      setSelectedIndex(index);
      setMessage("Nice! You found the imposter.");
      // if this was the last round, mark finished
      if (currentRoundIndex === rounds.length - 1) {
        setFinishedAllRounds(true);
      }
    } else {
      const newMistakes = mistakesLeft - 1;
      setMistakesLeft(newMistakes);
      setWrongIndices((prev) => [...prev, index]);

      if (newMistakes <= 0) {
        setGameOver(true);
        setMessage("Game over. No mistakes left.");
      } else {
        setMessage("Nope. Try again.");
      }
    }
  };

  const handleNextRound = () => {
    if (currentRoundIndex >= rounds.length - 1) return;

    setCurrentRoundIndex((prev) => prev + 1);
    setSelectedIndex(null);
    setWrongIndices([]);
    setMessage("");
  };

  const handleRestart = () => {
    setRounds(buildRounds());
    setCurrentRoundIndex(0);
    setMistakesLeft(MAX_MISTAKES);
    setSelectedIndex(null);
    setWrongIndices([]);
    setMessage("");
    setGameOver(false);
    setFinishedAllRounds(false);
  };

  const totalRounds = rounds.length;
  const roundNumber = currentRoundIndex + 1;

  return (
    <div className="app-root">
      <main className="game-container">
        <h1 className="game-title">Imposter</h1>

        {currentRound && (
          <>
            <div className="category">{currentRound.category}</div>

            <div className="cards-row">
              {currentRound.cards.map((card, index) => {
                const isSelected = index === selectedIndex;
                const isWrong = wrongIndices.includes(index);
                const isDisabled =
                  gameOver || finishedAllRounds || selectedIndex !== null;

                let cardClass = "card";
                if (isSelected) cardClass += " correct";
                else if (isWrong) cardClass += " wrong";
                if (isDisabled) cardClass += " disabled";

                return (
                  <button
                    key={index}
                    className={cardClass}
                    onClick={() => handleCardClick(card, index)}
                  >
                    {card.text}
                  </button>
                );
              })}
            </div>

            <div className="status">
              <div className="status-line">
                Round {roundNumber} of {totalRounds}
              </div>
              <div className="status-line">
                Mistakes Remaining: {mistakesLeft}
              </div>
              <div className="message">{message}</div>
            </div>

            {/* Show Next Round only after correct pick and if there are more rounds */}
            {!gameOver &&
              !finishedAllRounds &&
              selectedIndex !== null &&
              roundNumber < totalRounds && (
                <button className="primary-btn" onClick={handleNextRound}>
                  Next Round
                </button>
              )}

            {/* Show Restart when game over or all rounds cleared */}
            {(gameOver || finishedAllRounds) && (
              <button className="secondary-btn" onClick={handleRestart}>
                Play Again
              </button>
            )}
          </>
        )}
      </main>
    </div>
  );
}

export default App;
