import React, { useState, useEffect } from "react";
import "./App.css";

const MAX_GUESSES = 6;

const WORD_BANK = [
  {
    target: "HAPPY",
    synonyms: ["GLAD", "CHEERFUL", "JOYFUL", "MERRY", "ELATED"],
    close: ["CONTENT", "UPBEAT", "SMILING"],
    hint: "Describes someone who feels joy or pleasure."
  },
  {
    target: "ANGRY",
    synonyms: ["MAD", "FURIOUS", "IRATE", "LIVID"],
    close: ["UPSET", "ANNOYED"],
    hint: "A strong feeling of displeasure."
  },
  {
    target: "SMART",
    synonyms: ["BRIGHT", "CLEVER", "ASTUTE", "SHREWD"],
    close: ["WISE", "QUICK"],
    hint: "Good at learning and solving problems."
  }
];

function randomWord() {
  return WORD_BANK[Math.floor(Math.random() * WORD_BANK.length)];
}

export default function App() {
  const [word, setWord] = useState(null);
  const [guesses, setGuesses] = useState([]); // { text, result }
  const [input, setInput] = useState("");
  const [remaining, setRemaining] = useState(MAX_GUESSES);
  const [status, setStatus] = useState({ text: "", type: "info" });
  const [gameOver, setGameOver] = useState(false);

  useEffect(() => {
    resetGame();
  }, []);

  function resetGame() {
    const w = randomWord();
    setWord(w);
    setGuesses([]);
    setRemaining(MAX_GUESSES);
    setGameOver(false);
    setInput("");
    setStatus({
      text: "Guess a word that matches the clue.",
      type: "info"
    });
  }

  function classifyGuess(guess) {
    const g = guess.trim().toUpperCase();
    if (g === word.target) return "secret"; // you guessed the exact word
    if (word.synonyms.includes(g)) return "correct";
    if (word.close.includes(g)) return "close";
    return "wrong";
  }

  function handleSubmit(e) {
    e.preventDefault();
    if (!word || gameOver) return;

    const guess = input.trim().toUpperCase();
    if (!guess) return;

    const type = classifyGuess(guess);
    const storedResult = type === "secret" ? "correct" : type;

    setGuesses((prev) => [...prev, { text: guess, result: storedResult }]);

    if (type === "correct" || type === "secret") {
      setStatus({
        text: `Nice! You found a good match for the secret word.`,
        type: "win"
      });
      setGameOver(true);
      setInput("");
      return;
    }

    const newRemaining = remaining - 1;
    setRemaining(newRemaining);

    if (newRemaining <= 0) {
      const syns = word.synonyms.join(", ");
      setStatus({
        text: `Out of guesses. The word was "${word.target}". Some synonyms: ${syns}.`,
        type: "lose"
      });
      setGameOver(true);
      setInput("");
      return;
    }

    if (type === "close") {
      setStatus({
        text: "Close in meaning. You're on the right track.",
        type: "info"
      });
    } else {
      setStatus({
        text: "Not quite. Try another word that fits the clue.",
        type: "info"
      });
    }

    setInput("");
  }

  function tileClass(result) {
    if (result === "correct") return "tile tile-correct";
    if (result === "close") return "tile tile-close";
    if (result === "wrong") return "tile tile-wrong";
    return "tile";
  }

  if (!word) return <h1 style={{ textAlign: "center" }}>Loading…</h1>;

  return (
    <div className="app">
      <h1 className="title">Synonymle</h1>

      <div className="hint-box">
        <div><strong>Clue:</strong> {word.hint}</div>
        <div><strong>Word length:</strong> {word.target.length}</div>
      </div>

      <div className="board">
        {guesses.map((g, index) => (
          <div className="row" key={index}>
            {g.text.split("").map((ch, i) => (
              <div key={i} className={tileClass(g.result)}>
                {ch}
              </div>
            ))}
          </div>
        ))}
      </div>

      <form onSubmit={handleSubmit} className="guess-form">
        <input
          value={input}
          placeholder={
            gameOver
              ? "Game over – start a new word"
              : "Enter a word that matches the clue"
          }
          onChange={(e) => setInput(e.target.value)}
          disabled={gameOver}
        />
        <button disabled={gameOver}>Guess</button>
      </form>

      <div className={`status ${status.type}`}>{status.text}</div>
      <div className="remaining">
        {remaining} guess{remaining === 1 ? "" : "es"} remaining
      </div>

      <button className="new-game" onClick={resetGame}>
        New Secret Word
      </button>
    </div>
  );
}
